/**
 * A generic class representing a configurable object with dynamic properties.
 */
type ConfigurableObject = Record<string, unknown>

/**
 * Represents the type definition for a set of props.
 */
type PropsType = Record<string, unknown>

/**
 * Type constant for unknown types.
 */
type UnknownType = unknown

export type { ConfigurableObject, PropsType, UnknownType }
